import React, { Component } from 'react';
import emailjs from 'emailjs-com';

export class Contact extends Component {
  constructor(props) {
    super(props);
    this.state = {value: ''};
    this.handleSubmit = this.handleSubmit.bind(this);
  }
     handleSubmit(event) {
        alert('A name was submitted: ' + event.target.email);
        event.preventDefault();
        emailjs.sendForm('default_service', 'template_49yMrNtD', event.target, 'user_jezpq79gZJgVOjwUgvmH')
        .then(function(response) {
           console.log('SUCCESS!', response.status, response.text);
           alert('Your mail is sent, I will be in touch shortly!')
        }, function(error) {
           console.log('FAILED...', error);
           alert('There seems to be a problem with my mail server, your email was unfortunately not sent.');
        });
        alert(JSON.stringify(event.target.email));
        document.getElementById("myform").reset();
      }
  render() {
      function sendEmail(e){
          e.preventDefault();
          emailjs.sendForm('default_service', 'template_49yMrNtD', e.target, 'user_jezpq79gZJgVOjwUgvmHa')
          .then(function(response) {
             console.log('SUCCESS!', response.status, response.text);
             alert('Your mail is sent, I will be in touch shortly!')
          }, function(error) {
             console.log('FAILED...', error);
             alert('There seems to be a problem with my mail server, your email was unfortunately not sent.');
            alert(e.target[0])
          });
          document.getElementById("myform").reset();
        };

    return (
        <div>
      <div id="contact">
  <div className="container">
    <div className="col-md-8">
      <div className="row" >
        <div className="section-title">
          <h2>Get In Touch</h2>
          <p>Please fill out the form below and I will get back to you as soon as possible.</p>
        </div>
        <form id = "myform" className="contact-form"  onSubmit={this.handleSubmit} value={this.state.value}>
          <div class="form-row">
            <div class="form-group col-md-4">
              <div className="form-group">
                <input type="text" id="name" className="form-control" placeholder="Name" required="required"/>
                <p className="help-block text-danger">{this.state.name}</p>
              </div>
            </div>
            <div class="form-group col-md-6">
              <div className="form-group">
                <input type="email" id="email" className="form-control" placeholder="Email" required="required" />
                <p className="help-block text-danger"></p>
              </div>
            </div>
          </div>
            <textarea name="message" id="message" className="form-control" rows="4" placeholder="Message" required></textarea>
            <p className="help-block text-danger"></p>
         <button type="submit"  value="Send" className="btn btn-custom btn-lg">Send Message</button>
        </form>
      </div>
    </div>
    <div className="col-md-3 col-md-offset-1 contact-info">
      <div className="contact-item">
        <h3>Contact Info</h3>
        <p><span><i className="fa fa-map-marker"></i> Address</span>{this.props.data ? this.props.data.address : 'loading'}</p>
      </div>
      <div className="contact-item">
        <p><span><i className="fa fa-phone"></i> Phone</span> {this.props.data ? this.props.data.phone : 'loading'}</p>
      </div>
      <div className="contact-item">
        <p><span><i className="fa fa-envelope-o"></i> Email</span> {this.props.data ? this.props.data.email : 'loading'}</p>
      </div>
      <div className="contact-item">
        <p>
        <a class="btn btn-info" role="button" href="https://www.linkedin.com/in/lianne-de-la-salle-72b51999/"><i className="fa fa-linkedin"></i> </a></p>
      </div>
      <div className="contact-item">
        <p>
          <a class="btn btn-info" role="button" href="https://github.com/liannedls"><i className = "fa fa-github"></i> Github</a>
        </p>
      </div>
    </div>
  </div>
</div>
<div id="footer">
  <div className="container text-center">
    <p>&copy;2018 Design by <a href="http://www.templatewire.com" rel="nofollow">TemplateWire</a> Issaaf Kattan </p>
  </div>
</div>
      </div>
    )
  }
}

export default Contact
